class SiameseNet(nn.Module):
    def __init__(self) -> None:
        super(SiameseNet, self).__init__()

        # self.encoder = torchvision.models.vgg11(progress=False) # vgg11 takes so much memory!

        self.encoder = torchvision.models.alexnet(progress=False)
        self.encoder.features[0] = nn.Conv2d(
            1, 64, kernel_size=(11, 11), stride=(4, 4), padding=(2, 2)
        )

    def forward(self, x1: torch.tensor, x2: torch.tensor) -> torch.tensor:
        nbatchs = x1.size(0)
        x1 = self.encoder(x1).view((nbatchs, -1))
        x2 = self.encoder(x2).view((nbatchs, -1))

        return x1, x2

class ContrastiveLoss(nn.Module):
    def __init__(
        self,
        margin:int,
        reduce:bool,
    ) -> None:
        super(ContrastiveLoss, self).__init__()

        if isinstance(margin, torch.Tensor):
            self.margin = margin
        else:
            self.margin = torch.tensor([margin], dtype=dtype).to(device)

        self.reduce = reduce

    def forward(
        self, x1: torch.Tensor, x2: torch.Tensor, y: torch.Tensor
    ) -> torch.Tensor:
        dist2 = torch.nn.functional.pairwise_distance(x1, x2, keepdim=True).pow(2)
        out = (1 - y) * dist2 + y * torch.clamp(self.margin - dist2, min=0.0)

        if self.reduce:
            out = 0.5 * out.mean()
        return out

def evaluate_siamese_network(dataset_loader, model, threshold):
    if model is None:
        model = torch.load(
            "../checkpoints_logo_dataset/checkpoint_24.pkl", map_location=device
        )

    model.eval()
    accuracy = 0
    true_positive = false_positive = true_negative = false_negative = 0
    Outcome = collections.namedtuple(
        "Outcome",
        [
            "accuracy",
            "true_positive",
            "true_negative",
            "false_positive",
            "false_negative",
        ],
    )

    dataset_loader = DataLoader(
        dataset=dataset,
        shuffle=False,
        num_workers=0,
        batch_size=nbatchs_per_ev,
    )

    loss_fn = ContrastiveLoss(margin=1, reduce=False)
    for x1, x2, y in dataset_loader:
        x1, x2, y = x1.to(device), x2.to(device), y.to(device)

        out1, out2 = model(x1, x2)
        dist = torch.nn.functional.pairwise_distance(out1, out2, keepdim=True)

        yhat = (dist < threshold)
        correct = yhat == y

        for c, yh in zip(correct, yhat):
            if yh:
                if c:
                    true_positive += 1
                else:
                    false_positive += 1
            else:
                if c:
                    true_negative += 1
                else:
                    false_negative += 1
            accuracy += int(c)

    return Outcome(
        accuracy=accuracy/y.size(0),
        true_positive=true_positive,
        false_positive=false_positive,
        true_negative=true_negative,
        false_negative=false_negative
    )
